SELECT AI.ANIMAL_ID, AI.ANIMAL_TYPE, AI.NAME FROM ANIMAL_INS AS AI JOIN ANIMAL_OUTS AS AO ON AO.ANIMAL_ID = AI.ANIMAL_ID WHERE (AI.SEX_UPON_INTAKE = 'Intact Female' OR AI.SEX_UPON_INTAKE = 'Intact Male') AND (AO.SEX_UPON_OUTCOME = 'Spayed Female' OR AO.SEX_UPON_OUTCOME = 'Neutered Male') ORDER BY AI.ANIMAL_ID ASC